import React from 'react';
 import { Formik, Form, Field, ErrorMessage } from 'formik';
 
 const Form2 = () => (
   <div>
     <h1>Please Fillout the form!</h1>
     <Formik
       initialValues={{ candName: '', candAge: '' }}
       validate={values => {
         const errors = {};
         if (!values.candName) {
           errors.candName = 'Candidate name   is empty';
         } else if (!values.candAge) {
           errors.candAge = 'Candidate Age   is empty';
         }else if(Number(values.candAge)<18){
           errors.candAge="Candidate Age should be greater than 18"
         }
         return errors;
       }}
       onSubmit={() => {
         console.log("Submitted")
       }}
     >
       {() =>{
         return (
         <Form>
           <Field type="text" name="candName" />
           <ErrorMessage name="candName" />
           <Field type="number" name="candAge" />
           <ErrorMessage name="candAge"  />
           <button type="submit">
             Submit
           </button>
         </Form>
         );
       }
      }
     </Formik>
   </div>
 );
 
 export default FOrm2;
