import React,{Component} from "react";

class Form extends Component{
    constructor(){
        super();
        this.state={
            candName:"",
            candAge:"",
            candRole:"",
            erros:{
                candName:"",
                candAge:"",  
            },
        }
    }
    handleChange=({target:{name,value}})=>{
        console.log(name,value);
       
        const errors={...this.state.erros};

        switch(name){
            case "candName":{
            if(!value){
                errors[name]="Candidate name is empty";
            }else{
                errors[name]="";
            }
            break;
            }
            case "candAge":{
                if(!value){
                    errors[name]="Candidate age is empty";
                } else if(Number(value)<18){
                    errors[name]="candidate age should not be less than 18"
                }else{
                    errors[name]="";
                }
                break;
            }
        }
        this.setState({[name]:value,errors})

    }

    handleSubmit=(e)=>{
        e.preventDefault();
        console.log(this.state);
}
    render(){
        return(
            <form onSubmit={this.handleSubmit}>
                <label>Candidate Name</label>
                <input type="text" name="candName" value={this.state.candName} onChange={this.handleChange}/>
                {this.state.erros.candName?this.state.erros.candName:""}
                <br/>
                <label>Candidate Age</label>
                <input type="number" name="candAge" value={this.state.candAge} onChange={this.handleChange}/><br/>
                <label>Candidate Role</label>
                <select name="candRole" value={this.state.candName} onChange={this.handleChange}>
                    <option value="react">React Dev</option>
                    <option value="node">Node Developer</option>
                    <option value="mern">MERN dev</option>
                </select>
                <br/>
                <input type="Submit"/>
            </form>
        )
    }
}

export default Form;
